import React, {Component} from 'react';
import sad from '../images/chilaquilSad.png'
import {cartasJugador,cartaActual,turno,estado,id_jugador,tamanioBaraja,cartasXJugador,paloOcho} from '../cartas.json';



class Game extends Component{
    constructor() {
    super();
    this.state = {
      cartasJugador,
      cartaActual,
      turno,
      estado,
      cartasXJugador,
      id_jugador,
      tamanioBaraja,
      paloOcho
    }

  }


    /*
    render(){
        return(

        <div className="mt-5 text-light display-2 text-center">
            Game
            <img src={sad} alt="Chilaquil"/>
        </div>

        );
    }*/


  tirarCarta(index) {
    if (window.confirm('¿Quieres tirar esta carta?')){
      this.setState({
      cartasJugador:this.state.cartasJugador.filter((e,i)=>{
          return i !== index;
        })
      });
    }
  }

  render (){

    const cincoCartas = this.state.cartasJugador.map((carta,i)=>{
    if (i<5){
      return(
        <div className="flex-col p-3" >
          <div className="card mt-4 ml-2 mr-2">
            <img className="d-block w-20" src={`${require('./BeetleRoyale/'+carta.id_carta+'.PNG')}`} alt="First slide"/>
          </div>
          <div className="card-footer">
          <button className="btn btn-danger" onClick={this.tirarCarta.bind(this,i)}>
            Tirar
          </button>
          </div>
        </div>
         )}
    });

    var item = 0

    const masCartas = this.state.cartasJugador.map((carta,i)=>{
      if(i>=(5+5*1) && i<=(5+5*(1+1)-1)){
        return(
        <div className="flex-col p-3" >
          <div className="card mt-4 ml-2 mr-2">
            <img className="d-block w-20" src={`${require('./BeetleRoyale/'+carta.id_carta+'.PNG')}`} alt="First slide"/>
          </div>
          <div className="card-footer">
          <button className="btn btn-danger" onClick={this.tirarCarta.bind(this,i)}>
            Tirar
          </button>
          </div>
          <div>item = {item}
        </div>
        </div>
        )
      }
    })

    const masSlides = this.state.cartasJugador.map((carta,i)=>{
      if (i>=5 ){
        if(i%5 === 0){
          return(
            <div className="carousel-item">
              <div className="d-flex flex-row justify-content-md-center ">
                {masCartas}
              </div>
            </div>
             )
       /* } else {
          return(
            <p>No entré</p>
             )*/
        }
      }
    });

    const jugadores = this.state.cartasXJugador.map((jugador,i)=>{
      return(
        <li class="list-group-item">{jugador.nombre}: {jugador.numeroCartas}</li>
         );
    });


    const botonTomar = this.state.cartasXJugador.map((jugador,i)=>{
      if(i===0){
        if(this.state.turno.toString() === this.state.id_jugador){
          return(<button className="btn btn-danger">
                Tomar 1 carta
              </button>)
        }else{
          return(<button className="btn btn-danger" disabled>
                Tomar 1 carta
              </button>)
        }
      }
    })

    const botonSaltar = this.state.cartasXJugador.map((jugador,i)=>{
      if(i===0){
        if(this.state.turno.toString() === this.state.id_jugador && this.state.tamanioBaraja === 0){
          return(<button className="btn btn-danger">
                Saltar turno
              </button>)
        }else{
          return(<button className="btn btn-danger" disabled>
                Saltar turno
              </button>)
        }
      }
    })

    const selectPalo = this.state.cartasXJugador.map((jugador,i)=>{
      if(i===0){
        if(this.state.turno.toString() === this.state.id_jugador && this.state.estado === "ochoActual"){
          return(<form>
              <div class="form-group">
                <select class="form-control" id="exampleFormControlSelect1">
                  <option>Selecciona palo</option>
                  <option>picas</option>
                  <option>corazones</option>
                  <option>treboles</option>
                  <option>diamantes</option>
                </select>
              </div>
            </form>)
        }else{
          return(<form>
              <div class="form-group">
                <select disabled class="form-control" id="exampleFormControlSelect1">
                  <option>Selecciona palo</option>
                  <option>picas</option>
                  <option>corazones</option>
                  <option>treboles</option>
                  <option>diamantes</option>
                </select>
              </div>
            </form>)
        }
      }
    })

    const paloActual = this.state.cartasXJugador.map((jugador,i)=>{
      if(i===0){
        if(this.state.paloOcho === ''){
          return(this.state.cartaActual.palo)
        }else{
          return(this.state.paloOcho)
        }
      }
    })



    return(
      <div className="App">

        {/*barra nav cartas actuales*/}
        <nav className="navbar navbar-dark bg-dark">
          <a className="navbar-brand text-light">
            Cartas totales <span className="badge badge-pill badge-light ml-2 mr-4">
                {this.state.cartasJugador.length}
              </span>
              Eres el jugador <span className="badge badge-pill badge-light ml-2 mr-4">
                {this.state.id_jugador.length}
              </span>
          </a>
        </nav>
        {/*termina barra nav cartas actuales*/}


        {/*inicia carrusel*/}
        {/*inicia codigo base*/}
        <div id="carouselExampleControls" className="carousel slide" data-ride="carousel">
          <div className="carousel-inner">
            <div className="carousel-item active">
              <div className="d-flex flex-row justify-content-md-center ">
