import React, {Component} from 'react';
import sad from '../images/chilaquilSad.png'

class Start extends Component{
    constructor(){
        super()
        this.state={
            titulo: "",

        };

        this.handleInput=this.handleInput.bind(this);
    }

    handleInput(e){
        console.log(e.target.value);
        const {value,name}=e.target;
        this.setState({
            [name]:value
        });

    }



    render(){
        return( 
           <div class="container text-white">

               <div class="col">

                   <div class="row-sm text-center display-1">
                        The Crazy 88
                   </div>

                   <div class="row-sm">
                       <div class="row">

                           <div class="col-2">
                               <div class="form-group">
                                   <label for="totalPlayers"># Jugadores</label>

                                   <select class="form-control" id="totalPlayers">
                                       <option>2</option>
                                       <option>3</option>
                                       <option>4</option>
                                       <option>5</option>
                                   </select>
                               </div>
                           </div>

                           <div class="col-10 span2">
                               <button type="submit" className="btn btn-primary btn-block">
                                    Nuevo Juego
                               </button>
                           </div>

                       </div>
                   </div>

                   <div class="row-sm">

                       <div class="row">

                           <div class="col">
                               <input
                               type="text"
                               name="gameNumber"
                               className="form-control"
                               placeholder="Numero de Juego"
                               onChange={this.handleInput}
                               />
                           </div>

                           <div class="col">
                               <button type="submit" className="btn btn-primary">
                                    Unirse a un Juego
                               </button>
                           </div>
                       </div>
                   </div>
               </div>
           </div>
           );
    }
}

export default Start
