import React, {Component} from 'react';
const axios = require('axios').default;

const url = 'http://54.89.233.135:8000'


class Start extends Component{
    constructor(){
        super()
        this.state={
            titulo: "",

        };

        this.handleInput=this.handleInput.bind(this);
        this.activateLasers=this.activateLasers.bind(this);
        this.newGameAPICall=this.newGameAPICall.bind(this);

    }

    handleInput(e){
        console.log(e.target.value);
        const {value,name}=e.target;
        this.setState({
            [name]:value
        });

    }


    async newGameAPICall(){
        const response =
      await axios.get(url)
    console.log(response.data)
}

    activateLasers(){
        this.newGameAPICall();
        //window.location.href="/enter";
    }

    render(){
        return( 
           <div className="container text-white">

               <div className="d-flex p-5 flex-column">

                   <div className="text-center display-1">
                        The Crazy 88
                   </div>

                   <div className="d-flex p-3 mt-5 flex-row justify-content-center">

                           <div className="col-md-3">
                               <div className="form-group mb-0">
                                   <label htmlFor="totalPlayers">
                                       NÃºmero de Jugadores
                                   </label>
                                   <select className="form-control" id="totalPlayers">
                                       <option>2</option>
                                       <option>3</option>
                                       <option>4</option>
                                       <option>5</option>
                                   </select>
                               </div>
                           </div>

                           <div className="col-md-3 ml-3 span2 align-self-end">
                               <button type="submit" className="btn btn-primary btn-block">
                                    Nuevo Juego
                               </button>
                           </div>

                   </div>

                   <div className="d-flex p-3 flex-row justify-content-center">


                           <div className="col-md-3">
                               <input
                               type="text"
                               name="gameNumber"
                               className="form-control"
                               placeholder="Numero de Juego"
                               onChange={this.handleInput}
                               />
                           </div>

                           <div className="col-md-3 ml-3 span2" >
                               <button onClick={this.activateLasers} className="btn btn-primary btn-block">
                                    Unirse a un Juego
                               </button>
                           </div>

                   </div>
               </div>
           </div>
           );
    }
}

export default Start
