import React, {Component} from 'react';
const axios = require('axios').default;

const url = 'http://18.219.15.251:8080/nuevo'
const testUrl = "https://catfact.ninja/fact"


class Start extends Component{
    constructor(props){
        super(props)
        this.state={
            num_jugadores : 2
        };

        this.handleInput=this.handleInput.bind(this);
        this.activateLasers=this.activateLasers.bind(this);
        this.getDataAxios=this.getDataAxios.bind(this);
        //this.newGameAPICall=this.newGameAPICall.bind(this);

    }

    handleInput(e){
        console.log(e.target.value);
        const {value,name}=e.target;
        this.setState({
            [name]:value
        });

    }

    async getDataAxios(){
        var num_jugadores = 3;
        var idJuego = 100;
        const response = await axios.post(
          url,
          { 'idJuego': 10, 'num_jugadores': 3 },
          { headers: { 'Content-Type': 'application/json' } }
        )
        if(response.data == "Procesado"){
            window.location.href="/enter";
        }
    }

    activateLasers(){
        console.log("Holi crayoli");
        this.getDataAxios();
        //window.location.href="/enter";
    }

    updateInputValue(evt){
         this.setState({
            num_jugadores: evt.target.value
            });

        console.log("Holi: " + this.num_jugadores);
    }

    render(){
        return( 
           <div className="container text-white">

               <div className="d-flex p-5 flex-column">

                   <div className="text-center display-1">
                        The Crazy 88
                   </div>
                   <div>
      Currently using React {React.version}
    </div>
                   <div className="d-flex p-3 mt-5 flex-row justify-content-center">

                           <div className="col-md-3">
                               <div className="form-group mb-0">
                                   <label htmlFor="totalPlayers">
                                       Número de Jugadores
                                   </label>
                                   <select className="form-control" id="totalPlayers" value={this.state.num_jugadores} onChange={evt => this.updateInputValue(evt)}>
                                       <option>2</option>
                                       <option>3</option>
                                       <option>4</option>
                                       <option>5</option>
                                   </select>
                               </div>
                           </div>

                           <div className="col-md-3 ml-3 span2 align-self-end">
                               <button type="submit" className="btn btn-primary btn-block">
                                    Nuevo Juego
                               </button>
                           </div>

                   </div>

                   <div className="d-flex p-3 flex-row justify-content-center">


                           <div className="col-md-3">
                               <input
                               type="text"
                               name="gameNumber"
                               className="form-control"
                               placeholder="Numero de Juego"
                               onChange={this.handleInput}
                               />
                           </div>

                           <div className="col-md-3 ml-3 span2" >
                               <button onClick={this.activateLasers} className="btn btn-primary btn-block">
                                    Unirse a un Juego
                               </button>
                           </div>

                   </div>
               </div>
           </div>
           );
    }
}

export default Start
