class Start extends Component{
    constructor(){
        super()
        this.state={
            titulo: "",

        };

        this.handleInput=this.handleInput.bind(this);
        //this.activateLasers=this.activateLasers.bind(this);

    }

    handleInput(e){
        console.log(e.target.value);
        const {value,name}=e.target;
        this.setState({
            [name]:value
        });

    }




    render(){
        return( 
           <div class="container text-white">

               <div class="d-flex p-5 flex-column">

                   <div class="text-center display-1">
                        The Crazy 88
                   </div>

                   <div class="d-flex p-3 mt-5 flex-row justify-content-center">

                           <div class="col-md-3">
                               <div class="form-group mb-0">
                                   <label for=""totalPlayers>
                                       NÃºmero de Jugadores
                                   </label>
                                   <select class="form-control" id="totalPlayers">
                                       <option>2</option>
                                       <option>3</option>
                                       <option>4</option>
                                       <option>5</option>
                                   </select>
                               </div>
                           </div>

                           <div class="col-md-3 ml-3 span2 align-self-end">
                               <button type="submit" className="btn btn-primary btn-block">
                                    Nuevo Juego
                               </button>
                           </div>

                   </div>

                   <div class="d-flex p-3 flex-row justify-content-center">


                           <div class="col-md-3">
                               <input
                               type="text"
                               name="gameNumber"
                               className="form-control"
                               placeholder="Numero de Juego"
                               onChange={this.handleInput}
                               />
                           </div>

                           <div class="col-md-3 ml-3 span2" >
                               <button onClick={activateLasers} className="btn btn-primary btn-block">
                                    Unirse a un Juego
                               </button>
                           </div>

                   </div>
               </div>
           </div>
           );
    }

}

